import "./App.css";
import "./components/css/ScoreBoard.css";
import React, { Component } from "react";
import BowlingGame from "./components/BowlingGame";
import ScoreBoardFrame from "./components/ScoreBoardFrame";
import Controls from "./components/Controls";
import NewGame from "./components/NewGame";

import { isGameOver, finalScore, pinsStillStanding} from './components/HelperFunctions';

class ScoreBoard extends Component {
  constructor() {
    super();
    this.bowlingGame = BowlingGame.createNewBowlingGame();
    this.state = {
      score: this.bowlingGame.score()
    };
  }

  rollBall = pins => {
    this.bowlingGame.roll(pins);
    this.setState({ score: this.bowlingGame.score() });
  };

  newGame = () => {
    this.bowlingGame.restartGame();
    this.setState({ score: this.bowlingGame.score() });
  };

  render() {
    const { score } = this.state;
    return (
      <div>
        <Controls
          handleRoll={this.rollBall}
          pinsUp={pinsStillStanding(this.bowlingGame.standingPins())}
          gameOver={isGameOver(this.state.score[9].gameover)}
          finalScore={finalScore(this.state.score[9].cumulativeScore)}
        />
        <div className="score-board">
          {[...Array(10)].map((o, i) => (
            <ScoreBoardFrame
              key={i}
              frameNumber={i + 1}
              leftBox={score[i].firstRollBox}
              rightBox={score[i].secondRollBox}
              extraBox={score[i].finalRollBox}
              score={score[i].cumulativeScore}
            />
          ))}
        </div>
        <NewGame handleReset={this.newGame} />
      </div>
    );
  }
}

const App = () => (
  <div className="App">
    <ScoreBoard />
  </div>
);

export default App;
