import {frameSum, isStrike, isSpare, bonusForStrike, bonusForSpare} from './HelperFunctions';

class BowlingGame {
  constructor() {
    this.rolls = [];
    this.currentRole = 0;
  }

  static create = () => new BowlingGame();

  roll = pins => (this.rolls[this.currentRole++] = pins);

  reset = () => {
    this.rolls = [];
    this.currentRole = 0;
  };

  standingPins = () => {
    const scoreData = this.score();
    let pinsUp = 10;
    scoreData.forEach(o => {
      if (o.pinsUp !== null && !isNaN(o.pinsUp)) {
        pinsUp = o.pinsUp;
      }
    });
    return pinsUp;
  };

  score = () => {
    let scoreData = [];
    let score = 0;
    let frameIndex = 0;

    const roll1 = () => this.rolls[frameIndex];
    const roll2 = () => this.rolls[frameIndex + 1];
    const roll3 = () => this.rolls[frameIndex + 2];

    const saveFrame = (scoreData, leftBox, rightBox, score, standingPins) => {
      if (scoreData.length < 9) {
        scoreData.push({
          leftBox,
          rightBox,
          cumulativeScore: score,
          standingPins
        });
      } else {
        const box1 = roll1() === 10 ? "X" : roll1();
        const box2 = roll2() === 10 ? "X" : isSpare() ? "/" : roll2();
        let box3;
        if (roll3() === 10) {
          box3 = "X";
        } else if (roll1() === 10 || roll1() + roll2() === 10) {
          box3 = roll3();
        } else {
          box3 = "";
        }

        scoreData.push({
          leftBox: box1,
          rightBox: box2,
          extraBox: box3,
          cumulativeScore: score,
          standingPins
        });
      }
    };

    [...Array(10)].forEach((_, frame) => {
      if (isStrike(roll1())) {
        score += 10 + bonusForStrike(roll2(), roll3());
        saveFrame(scoreData, "", "X", score, 10);
        frameIndex++;
      } else if (isSpare(roll1(), roll2())) {
        score += 10 + bonusForSpare(roll3());
        saveFrame(scoreData, roll1(), "/", score, 10);
        frameIndex += 2;
      } else {
        score += frameSum(roll1(), roll2());
        const standingPins = roll2() !== undefined ? 10 : 10 - roll1();
        saveFrame(scoreData, roll1(), roll2(), score, standingPins);
        frameIndex += 2;
      }
    });

    return scoreData;
  };
}

export default BowlingGame;
