class BowlingGame {
  constructor() {
    this.rolls = [];
    this.currentRole = 0;
  }

  static create = () => new BowlingGame();

  roll = pins => (this.rolls[this.currentRole++] = pins);

  reset = () => {
    this.rolls = [];
    this.currentRole = 0;
  };

  standingPins = () => {
    const scoreData = this.score();
    let standingPins = 10;
    scoreData.forEach(o => {
      if (o.standingPins !== null && !isNaN(o.standingPins)) {
        standingPins = o.standingPins;
      }
    });
    return standingPins;
  };

  bowlType = ({roll1, roll2, roll3}) => {
    let type;

    if (roll1 === 10) {
      type = 'strike';
    }
    if ((roll1 + roll2) === 10) {
      type = 'spare';
    }

    return type;

  }
  score = () => {
    let scoreData = [];
    let score = 0;
    let frameIndex = 0;

    const roll1 = () => this.rolls[frameIndex];
    const roll2 = () => this.rolls[frameIndex + 1];
    const roll3 = () => this.rolls[frameIndex + 2];

    const saveFrame = (scoreData, leftBox, rightBox, score, standingPins) => {
      if (scoreData.length < 9) {
        scoreData.push({
          leftBox,
          rightBox,
          cumulativeScore: score,
          standingPins
        });
      } else {
        const box1 = roll1() === 10 ? "X" : roll1();
        const box2 = roll2() === 10 ? "X" : isSpare() ? "/" : roll2();
        let box3;
        if (roll3() === 10) {
          box3 = "X";
        } else if (roll1() === 10 || roll1() + roll2() === 10) {
          box3 = roll3();
        } else {
          box3 = "";
        }

        scoreData.push({
          leftBox: box1,
          rightBox: box2,
          cumulativeScore: score,
          standingPins,
          extraBox: box3
        });
      }
    };

    [...Array(10)].forEach((_, frame) => {

      switch (bowlType(roll1, roll2, roll3)) {

        case 'strike':
         score += 10 + roll2() + roll3();
         saveFrame(scoreData, "", "X", score, 10);
         frameIndex++;
          break;
        case 'spare':
          score += 10 + roll3();
          saveFrame(scoreData, roll1(), "/", score, 10);
          frameIndex += 2;
          break;
        default:
          score += roll1() + roll2();
          const standingPins = roll2() !== undefined ? 10 : 10 - roll1();
          saveFrame(scoreData, roll1(), roll2(), score, standingPins);
          frameIndex += 2;
      }

    });

    return scoreData;
  };
}

export default BowlingGame;
