import { frameSum, isStrike, isSpare, bonusForStrike, bonusForSpare} from './HelperFunctions';

class BowlingGame {
  constructor() {
    this.rolls = [];
    this.currentRoll = 0;
    this.maxScore = 10;
  }

  static createNewBowlingGame = () => new BowlingGame();

  roll = pins => (this.rolls[this.currentRoll++] = pins);

  standingPins = () => {
    const bowlingScore = this.score();
    let stillStanding = this.maxScore;
    bowlingScore.forEach(score => {
      if (score.pinsStillStanding !== null && !isNaN(score.pinsStillStanding)) {
        stillStanding = score.pinsStillStanding;
      }
    });
    return stillStanding;
  };

  /*
    If we click on reset game, reset everything.
  */
  restartGame = () => {
    this.rolls = [];
    this.currentRoll = 0;
  };

  score = () => {
    let bowlingScore = [];
    let score = 0;
    let frameIndex = 0;

    const roll1 = () => this.rolls[frameIndex];
    const roll2 = () => this.rolls[frameIndex + 1];
    const roll3 = () => this.rolls[frameIndex + 2];

    const saveFrame = (bowlingScore, firstRollBox, secondRollBox, score, pinsStillStanding) => {
      if (bowlingScore.length < 9) {
        bowlingScore.push({
          firstRollBox,
          secondRollBox,
          cumulativeScore: score,
          pinsStillStanding
        });
      } else {
        const scoreBoardBox1 = roll1() === this.maxScore ? "X" : roll1();
        const scoreBoardBox2 = roll2() === this.maxScore ? "X" : isSpare() ? "/" : roll2();
        let scoreBoardExtraBox;
        if (roll3() === this.maxScore) {
          scoreBoardExtraBox = "X";
        } else if (roll1() === this.maxScore || roll1() + roll2() === this.maxScore) {
          scoreBoardExtraBox = roll3();
        } else {
          scoreBoardExtraBox = "";
        }

        bowlingScore.push({
          firstRollBox: scoreBoardBox1,
          secondRollBox: scoreBoardBox2,
          cumulativeScore: score,
          pinsStillStanding,
          finalRollBox: scoreBoardExtraBox,
          gameover: isNaN(score) ? false : true
        });
      }
    };

    [...Array(this.maxScore)].forEach((_, frame) => {
      if (isStrike(roll1())) {
        score += this.maxScore + bonusForStrike(roll2(), roll3());
        saveFrame(bowlingScore, "", "X", score, this.maxScore);
        frameIndex++;
      } else if (isSpare(roll1(), roll2())) {
        score += this.maxScore + bonusForSpare(roll3());
        saveFrame(bowlingScore, roll1(), "/", score, this.maxScore);
        frameIndex += 2;
      } else {
        score += frameSum(roll1(), roll2());
        const pinsUp = roll2() !== undefined ? this.maxScore : this.maxScore - roll1();
        saveFrame(bowlingScore, roll1(), roll2(), score, pinsUp);
        frameIndex += 2;
      }
    });

    return bowlingScore;
  };
}

export default BowlingGame;
