import {frameSum, isStrike, isSpare} from './HelperFunctions';

class BowlingGame {
  constructor() {
    this.rolls = [];
    this.currentRoll = 0;
  }

  static create = () => new BowlingGame();

  roll = pins => (this.rolls[this.currentRoll++] = pins);

  restartGame = () => {
    this.rolls = [];
    this.currentRoll = 0;
  };

  standingPins = () => {
    const scoreData = this.score();
    let standingPins = 10;
    scoreData.forEach(o => {
      if (o.standingPins !== null && !isNaN(o.standingPins)) {
        standingPins = o.standingPins;
      }
    });
    return standingPins;
  };


  score = () => {
    let scoreData = [];
    let score = 0;
    let frameIndex = 0;
    let maxScore = 10;

    const roll1 = () => this.rolls[0];
    const roll2 = () => this.rolls[1];
    const roll3 = () => this.rolls[2];

    const frameTotal = () => roll1() + roll2();

    const spareBonus = () => roll3();

    const strikeBonus = () => roll2() + roll3();

    const isStrike = () => roll1() === maxScore;

    const isSpare = () => frameTotal() === maxScore;

    const saveFrame = (scoreData, leftBox, rightBox, score, standingPins) => {
      if (scoreData.length < 9) {
        scoreData.push({
          leftBox,
          rightBox,
          cumulativeScore: score,
          standingPins
        });
      } else {
        const box1 = roll1() === maxScore ? "X" : roll1();
        const box2 = roll2() === maxScore ? "X" : isSpare() ? "/" : roll2();
        let box3;
        if (roll3() === maxScore) {
          box3 = "X";
        } else if (roll1() === maxScore || roll1() + roll2() === maxScore) {
          box3 = roll3();
        } else {
          box3 = "";
        }

        scoreData.push({
          leftBox: box1,
          rightBox: box2,
          cumulativeScore: score,
          standingPins,
          extraBox: box3
        });
      }
    };

    [...Array(10)].forEach((_, frame) => {
      if (isStrike()) {
        score += 10 + strikeBonus();
        saveFrame(scoreData, "", "X", score, maxScore);
        frameIndex++;
      } else if (isSpare()) {
        score += 10 + spareBonus();
        saveFrame(scoreData, roll1(), "/", score, maxScore);
        frameIndex += 2;
      } else {
        score += frameTotal();
        const standingPins = roll2() !== undefined ? maxScore : maxScore - roll1();
        saveFrame(scoreData, roll1(), roll2(), score, standingPins);
        frameIndex += 2;
      }
    });

    return scoreData;
  };
}

export default BowlingGame;
