import "./App.css";
import "./components/css/ScoreBoard.css";
import React, { Component } from "react";
import BowlingGame from "./components/BowlingGame";
import ScoreBoardFrame from "./components/ScoreBoardFrame";


const Controls = ({ handleRoll, handleReset, pinsUp }) => (
  <div className="controls">
    <div className="controls-left">
      {[...Array(pinsUp + 1)].map((o, i) => (
        <button key={i} className="roll" onClick={() => handleRoll(i)}>
          {i}
        </button>
      ))}
    </div>
    <div className="controls-right">
      <div className="roll reset" onClick={() => handleReset()}>
        Reset Game {pinsUp}
      </div>
    </div>
  </div>
);

class ScoreBoard extends Component {
  constructor() {
    super();
    this.game = BowlingGame.create();
    this.state = {
      score: this.game.score()
    };
  }

  roll = pins => {
    this.game.roll(pins);
    this.setState({ score: this.game.score() });
  };

  reset = () => {
    this.game.reset();
    this.setState({ score: this.game.score() });
  };

  pinsUp = () => this.game.standingPins();

  render() {
    const { score } = this.state;

    return (
      <div>
        <Controls handleRoll={this.roll} handleReset={this.reset} pinsUp={this.pinsUp()} />

        <div className="score-board">
          {[...Array(10)].map((o, i) => (
            <ScoreBoardFrame
              key={i}
              frameNumber={i + 1}
              leftBox={score[i].leftBox}
              rightBox={score[i].rightBox}
              extraBox={score[i].extraBox}
              score={score[i].cumulativeScore}
            />
          ))}
        </div>
      </div>
    );
  }
}

const App = () => (
  <div className="App">
    <ScoreBoard />
  </div>
);

export default App;
