import "./App.css";
import "./components/css/ScoreBoard.css";
import React, { Component } from "react";
import BowlingGame from "./components/BowlingGame";
import ScoreBoardFrame from "./components/ScoreBoardFrame";
import Controls from "./components/Controls";



class ScoreBoard extends Component {
  constructor() {
    super();
    this.bowlingGame = BowlingGame.createNewBowlingGame();
    this.state = {
      score: this.bowlingGame.score()
    };
  }

  roll = pins => {
    this.bowlingGame.roll(pins);
    this.setState({ score: this.bowlingGame.score() });
  };

  reset = () => {
    this.bowlingGame.reset();
    this.setState({ score: this.bowlingGame.score() });
  };

  pinsUp = () => this.bowlingGame.standingPins();

  render() {
    const { score } = this.state;

    return (
      <div>
        <Controls handleRoll={this.roll} handleReset={this.reset} pinsUp={this.pinsUp()} />

        <div className="score-board">
          {[...Array(10)].map((o, i) => (
            <ScoreBoardFrame
              key={i}
              frameNumber={i + 1}
              leftBox={score[i].leftBox}
              rightBox={score[i].rightBox}
              extraBox={score[i].extraBox}
              score={score[i].cumulativeScore}
            />
          ))}
        </div>
      </div>
    );
  }
}

const App = () => (
  <div className="App">
    <ScoreBoard />
  </div>
);

export default App;
