//What is the sum of all of the frames?
export function frameSum(roll1, roll2) {
  return roll1 + roll2;
}
//Was this a strike?
export function isStrike(roll1) {
  return roll1 === 10;
}
//Was this a spare?
export function isSpare(roll1, roll2) {
  return frameSum(roll1, roll2) === 10;
}
//Is there a bonus for a strike?
export function bonusForStrike(roll2, roll3) {
  return roll2 + roll3;
}
//Return spare bonus.
export function bonusForSpare(roll3) {
  return roll3;
}
//Is the game over when final score is calcualted?
export function isGameOver(gameover) {
  return gameover;
}
//Calculate Final ScoreBoard
export function finalScore(score) {
  return score;
}
//How many pins are still standing?
export function pinsStillStanding(pins) {
  return pins;
}
