import "./components/css/ScoreBoard.css";
import React, { Component } from "react";

class ScoreBoardFrame extends Component {
  constructor() {
    super();
    this.newBowlingGame = BowlingGame.create();
    this.state = {
      score: this.newBowlingGame.score()
    };
  }


  render() {
    return (
      <div className="frame">
        <div className="frame-number">{this.props.frameNumber}</div>
        <div className="frame-score">
          <div className="box left">{this.props.leftBox}</div>
          <div className="box right">{this.props.rightBox}</div>
          <div className="box extra">{this.props.extraBox}</div>
        </div>
        <div className="running-score">{!isNaN(this.props.score) && this.props.score}</div>
      </div>
    )
  }
}

export default ScoreBoardFrame;
